
CREATE TABLE `yii_parr` (
  `p_id` int(11) NOT NULL COMMENT 'Word ID',
  `doit` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Show it',
  `eng` varchar(48) NOT NULL COMMENT 'Phrasal Verb',
  `key1` varchar(24) NOT NULL COMMENT 'Verb',
  `key2` varchar(24) NOT NULL COMMENT 'Postposition',
  `ukr` varchar(50) NOT NULL COMMENT 'Ukrainian Translate',
  `rus` varchar(50) NOT NULL COMMENT 'Russian Translate',
  `descr` varchar(256) NOT NULL COMMENT 'English Description',
  `exam` varchar(256) NOT NULL COMMENT 'English Example',
  `syn` varchar(100) NOT NULL COMMENT 'Synonym',
  `ant` varchar(100) NOT NULL COMMENT 'Antonym',
  `image` varchar(100) NOT NULL COMMENT 'Image'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


